    private void btHitungAlphaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btHitungAlphaActionPerformed
        if (Integer.parseInt(totalHari.getText())>31 | Integer.parseInt(jumlahKehadiran.getText())>31 ) {
          jumlahAlpha.setText("Tidak boleh > 31");
        } else if(Integer.parseInt(jumlahKehadiran.getText()) > Integer.parseInt(totalHari.getText())) {
          jumlahAlpha.setText("Error input");
        } else {
          int jmlAlpha = Integer.parseInt(totalHari.getText()) - Integer.parseInt(jumlahKehadiran.getText());
          jumlahAlpha.setText(Integer.toString(jmlAlpha));
        }
    }//GEN-LAST:event_btHitungAlphaActionPerformed

    private void NIPActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NIPActionPerformed
        Object selectedItem =NIP.getSelectedItem();
        if (selectedItem != null ) {
            String selectedItemStr = selectedItem.toString();
            String thn = new String(selectedItemStr);
            String getTahun = thn.substring(0, 4);
            String getKode = thn.substring(4, 6);
            int Tahun = Integer.parseInt(getTahun);
            int Kode = Integer.parseInt(getKode);
                if (Tahun == 2020) {
                    namaPegawai.setText("Hilda Amalia");
                    tahunMasuk.setText(getTahun);
//                    Jabatan.setText(getKode);
                        if (Kode == 01) {
                            Jabatan.setText("Kepala Divisi");
                            gajiPokok.setText("10000000");
                        } else if (Kode == 02) {
                            Jabatan.setText("Kepala Bagian");
                            gajiPokok.setText("7000000");
                        } else if (Kode == 03) {
                            Jabatan.setText("Staff Bagian");
                            gajiPokok.setText("4000000");
                        }
                } else if (Tahun == 2017) {
                    namaPegawai.setText("Deni Pribadi");
                    tahunMasuk.setText(getTahun);
                        if (Kode == 01) {
                            Jabatan.setText("Kepala Divisi");
                            gajiPokok.setText("10000000");
                        } else if (Kode == 02) {
                            Jabatan.setText("Kepala Bagian");
                            gajiPokok.setText("7000000");
                        } else if (Kode == 03) {
                            Jabatan.setText("Staff Bagian");
                            gajiPokok.setText("4000000");
                        }
                } else if (Tahun == 2001) {
                    namaPegawai.setText("Yuri Safitri");
                    tahunMasuk.setText(getTahun);
                        if (Kode == 01) {
                            Jabatan.setText("Kepala Divisi");
                            gajiPokok.setText("10000000");
                        } else if (Kode == 02) {
                            Jabatan.setText("Kepala Bagian");
                            gajiPokok.setText("7000000");
                        } else if (Kode == 03) {
                            Jabatan.setText("Staff Bagian");
                            gajiPokok.setText("4000000");
                        }
                } else if (Tahun == 2005) {
                    namaPegawai.setText("Iqbal Alif");
                    tahunMasuk.setText(getTahun);
                        if (Kode == 01) {
                            Jabatan.setText("Kepala Divisi");
                            gajiPokok.setText("10000000");
                        } else if (Kode == 02) {
                            Jabatan.setText("Kepala Bagian");
                            gajiPokok.setText("7000000");
                        } else if (Kode == 03) {
                            Jabatan.setText("Staff Bagian");
                            gajiPokok.setText("4000000");
                        }
                }
                
        }// TODO add your handling code here:
    }//GEN-LAST:event_NIPActionPerformed

