    private void btHitungTunjanganActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btHitungTunjanganActionPerformed
        if (Integer.parseInt(jumlahAlpha.getText()) > 3) {
            Kehadiran.setText("0");
            double satuPersen = 0.01 ; // jumlah persen
            double duaPuluhPersen = 0.2;
              
            double tunjanganMakan = ( Integer.parseInt(gajiPokok.getText()) * satuPersen ) * Integer.parseInt(jumlahKehadiran.getText());
            double tunjanganTransport = ( Integer.parseInt(gajiPokok.getText()) * duaPuluhPersen );
              
            Makan.setText(Double.toString(tunjanganMakan));
            Transport.setText(Double.toString(tunjanganTransport));
              
        } else {
            Kehadiran.setText("500000");
            double satuPersen = 0.01 ; // jumlah persen
            double duaPuluhPersen = 0.2;
              
            double tunjanganMakan = ( Integer.parseInt(gajiPokok.getText()) * satuPersen ) * Integer.parseInt(jumlahKehadiran.getText());
            double tunjanganTransport = ( Integer.parseInt(gajiPokok.getText()) * duaPuluhPersen );
              
            Makan.setText(Double.toString(tunjanganMakan));
            Transport.setText(Double.toString(tunjanganTransport));
        }
    }//GEN-LAST:event_btHitungTunjanganActionPerformed

    private void totalJumlahGajiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_totalJumlahGajiActionPerformed
        if ("".equals(nilaiPinjaman.getText())) {
            nilaiPinjaman.setText("0");
            double pph = Integer.parseInt(gajiPokok.getText()) * 0.5;
            double totalGaji = 
               (Integer.parseInt(gajiPokok.getText()) + 
                Integer.parseInt(Kehadiran.getText()) +
                Double.parseDouble(Makan.getText()) +
                Double.parseDouble(Transport.getText()))
                - pph - Integer.parseInt(nilaiPinjaman.getText());
        
        txTotalGaji.setText(Double.toString(totalGaji));
        } else {
        double pph = Integer.parseInt(gajiPokok.getText()) * 0.5;
        double totalGaji = 
               (Integer.parseInt(gajiPokok.getText()) + 
                Integer.parseInt(Kehadiran.getText()) +
                Double.parseDouble(Makan.getText()) +
                Double.parseDouble(Transport.getText()))
                - pph - Integer.parseInt(nilaiPinjaman.getText());
        
        txTotalGaji.setText(Double.toString(totalGaji));
        }
    }//GEN-LAST:event_totalJumlahGajiActionPerformed

